import React, { useState } from 'react';

const EnrollForm = ({ courseName }) => {
  const [formData, setFormData] = useState({
    name: '',
    country: '',
    mobile: '',
    email: '',
    course: courseName || '',
    duration: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);
    // Add API call to submit form data to the backend
  };

  return (
    <form onSubmit={handleSubmit} style={styles.form}>
      <h2>Enroll Now</h2>
      <input
        type="text"
        name="name"
        placeholder="Name"
        value={formData.name}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="country"
        placeholder="Country"
        value={formData.country}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="mobile"
        placeholder="Mobile"
        value={formData.mobile}
        onChange={handleChange}
        required
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleChange}
        required
      />
      <select
        name="course"
        value={formData.course}
        onChange={handleChange}
        required
      >
        <option value="">Select Course</option>
        <option value="AWS">AWS</option>
        <option value="Linux">Linux</option>
        <option value="AWS Devops">AWS Devops</option>
        <option value="Python">Python</option>
        <option value="Container">Container</option>
        <option value="AWS Data Platform">AWS Data Platform</option>
        <option value="AWS DevSecOps">AWS DevSecOps</option>
      </select>
      <input
        type="text"
        name="duration"
        placeholder="Duration"
        value={formData.duration}
        onChange={handleChange}
        required
      />
      <button type="submit">Submit</button>
    </form>
  );
};

const styles = {
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
    maxWidth: '300px',
    margin: '0 auto',
  },
};

export default EnrollForm;
