import React from 'react';

const About = () => {
  return (
    <div style={styles.container}>
      <div style={styles.content}>
        <h1>About Me</h1>
        <p>
          Hi, I'm Rahul Ranjan, an AWS DevOps Engineer with 8+ years of hands-on experience in Cloud Computing and DevOps.
        </p>
        <h2>Expertise:</h2>
        <ul>
          <li>Proficient in Linux, AWS, DevOps tools, Python, Jenkins, Terraform, Kubernetes, and EKS.</li>
          <li>Specializes in designing scalable cloud solutions, implementing CI/CD pipelines, and managing containerized applications.</li>
        </ul>
        <h2>Teaching Philosophy:</h2>
        <ul>
          <li>Passionate about simplifying complex concepts for learners.</li>
          <li>Focus on hands-on training with real-world scenarios.</li>
        </ul>
        <h2>Achievements:</h2>
        <ul>
          <li>Trained 100+ students and professionals, helping them excel in Cloud and DevOps roles.</li>
          <li>Mentored multiple students to achieve certifications like AWS Certified Solutions Architect and Kubernetes Administrator.</li>
        </ul>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh', // Full page height
    backgroundColor: '#1e3a8a', // Dark blue background color
    color: '#ffffff', // White text color
    padding: '20px',
  },
  content: {
    maxWidth: '800px',
    width: '100%',
    backgroundColor: '#2563eb', // Slightly lighter blue for content box
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  },
};

export default About;
