import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import '../styles/EnrollForm.css';
import { motion } from 'framer-motion';
import { Button, Card, Input, Select } from '@/components/ui';

const courseDurations = {
  AWS: '9 weeks',
  Linux: '7 weeks',
  'AWS DevOps': '15 weeks',
  Python: '7 weeks',
  Container: '10 weeks',
  'AWS Data Platform': '8 weeks',
  'AWS DevSecOps': '12 weeks',
};

const EnrollForm = () => {
  const location = useLocation();
  const courseName = location.state?.courseName || '';

  const [formData, setFormData] = useState({
    name: '',
    country: '',
    city: '',
    mobile: '',
    email: '',
    course: courseName,
    duration: courseDurations[courseName] || '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
      ...(name === 'course' && { duration: courseDurations[value] || '' }),
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);
    // Add API call to submit form data to the backend
  };

  return (
    <motion.div className="enroll-form-container" initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }}>
      <Card className="p-6 max-w-lg mx-auto shadow-lg">
        <h2 className="text-2xl font-bold text-center mb-4">Enroll Now</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <Input type="text" name="name" placeholder="Enter your name" value={formData.name} onChange={handleChange} required label="Name" />
          <Input type="text" name="country" placeholder="Enter your country" value={formData.country} onChange={handleChange} required label="Country" />
          <Input type="text" name="city" placeholder="Enter your city" value={formData.city} onChange={handleChange} required label="City" />
          <Input type="text" name="mobile" placeholder="Enter your mobile number" value={formData.mobile} onChange={handleChange} required label="Mobile" />
          <Input type="email" name="email" placeholder="Enter your email" value={formData.email} onChange={handleChange} required label="Email" />
          <Select name="course" value={formData.course} onChange={handleChange} required label="Course">
            <option value="">Select Course</option>
            {Object.keys(courseDurations).map((course) => (
              <option key={course} value={course}>{course}</option>
            ))}
          </Select>
          <Input type="text" name="duration" value={formData.duration} readOnly label="Duration" />
          <Button type="submit" className="w-full">Submit</Button>
        </form>
      </Card>
    </motion.div>
  );
};

export default EnrollForm;
