import React from 'react';
import EnrollForm from '../components/EnrollForm';
import '../styles/Home.css'; // Import custom styles

const courses = [
  { id: 1, name: 'AWS', duration: '6 weeks', topics: ['EC2', 'S3', 'Lambda'] },
  { id: 2, name: 'Linux', duration: '4 weeks', topics: ['Shell Scripting', 'File Systems', 'Networking'] },
  { id: 3, name: 'AWS Devops', duration: '8 weeks', topics: ['CI/CD', 'CloudFormation', 'CodePipeline'] },
  { id: 4, name: 'Python', duration: '5 weeks', topics: ['Basics', 'OOP', 'Django'] },
  { id: 5, name: 'Container', duration: '3 weeks', topics: ['Docker', 'Kubernetes', 'ECS'] },
  { id: 6, name: 'AWS Data Platform', duration: '7 weeks', topics: ['Redshift', 'Glue', 'Athena'] },
  { id: 7, name: 'AWS DevSecOps', duration: '6 weeks', topics: ['Security Best Practices', 'IAM', 'KMS'] },
];

const Home = () => {
  return (
    <div className="home-container">
      <h1 className="main-heading">MASTER DEVOPS & CLOUD with Rahul</h1>
      <p className="sub-heading">Learn the latest technologies and advance your career.</p>

      <div className="courses-catalog">
        {courses.map((course) => (
          <div key={course.id} className="course-card">
            <h3>{course.name}</h3>
            <p><strong>Duration:</strong> {course.duration}</p>
            <ul>
              {course.topics.map((topic, index) => (
                <li key={index}>{topic}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>

      <EnrollForm />
    </div>
  );
};

export default Home;
