import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Home.css'; // Import custom styles

const courses = [
  { id: 1, name: 'AWS', duration: '6 weeks', topics: ['EC2', 'S3', 'Lambda'] },
  { id: 2, name: 'Linux', duration: '4 weeks', topics: ['Shell Scripting', 'File Systems', 'Networking'] },
  { id: 3, name: 'AWS DevOps', duration: '8 weeks', topics: ['CI/CD', 'CloudFormation', 'CodePipeline'] },
  { id: 4, name: 'Python', duration: '5 weeks', topics: ['Basics', 'OOP', 'Django'] },
  { id: 5, name: 'Container', duration: '3 weeks', topics: ['Docker', 'Kubernetes', 'ECS'] },
  { id: 6, name: 'AWS Data Platform', duration: '7 weeks', topics: ['Redshift', 'Glue', 'Athena'] },
  { id: 7, name: 'AWS DevSecOps', duration: '6 weeks', topics: ['Security Best Practices', 'IAM', 'KMS'] },
];

const Home = () => {
  const navigate = useNavigate();

  const handleEnrollClick = (courseName) => {
    navigate('/enroll', { state: { courseName } }); // Navigate to enroll form with course name
  };

  return (
    <div className="home-container">
      <h1 className="main-heading">Techcloud Academy</h1>
      <h2 className="sub-heading">MASTER DEVOPS & CLOUD with Rahul</h2>
      <p className="description">Learn the latest technologies and advance your career.</p>

      {/* Who Should Enroll Section */}
      <div className="plain-section">
        <h3 className="plain-heading">Who Should Enroll?</h3>
        <ul className="plain-list">
          <li>Students and new graduates aiming to start a career in Cloud.</li>
          <li>Professionals seeking to transition into Cloud or DevOps.</li>
          <li>Developers looking to specialize in AWS, DevOps, or modern technologies.</li>
        </ul>
      </div>

      {/* Course Catalog */}
      <h2 className="courses-heading">Currently Offering Below Courses</h2>
      <div className="courses-catalog">
        {courses.map((course) => (
          <div key={course.id} className="course-card">
            <h3>{course.name}</h3>
            <p><strong>Duration:</strong> {course.duration}</p>
            <ul>
              {course.topics.map((topic, index) => (
                <li key={index}>{topic}</li>
              ))}
            </ul>
            <button
              className="enroll-button"
              onClick={() => handleEnrollClick(course.name)}
            >
              Enroll Now
            </button>
          </div>
        ))}
      </div>

      {/* Your Future in Cloud and DevOps Section */}
      <div className="plain-section">
        <h3 className="plain-heading">Your Future in Cloud and DevOps</h3>
        <ul className="plain-list">
          <li><strong>Industry Growth:</strong> The Cloud computing industry is growing at a projected rate of 15% annually.</li>
          <li><strong>High Demand:</strong> AWS, Kubernetes, and DevOps professionals are among the most sought-after in IT.</li>
          <li><strong>Job Placement Support:</strong> Dedicated help to secure roles in top tech companies.</li>
        </ul>
      </div>
    </div>
  );
};

export default Home;
